import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import { useNavigate } from 'react-router-dom';

export default function Home() {
  const navigate = useNavigate();

  const handleNavigate = (role) => {
    if (role === 'Admin') navigate('/admin-dashboard');
    else if (role === 'Artist') navigate('/artist-dashboard');
    else if (role === 'Customer') navigate('/customer-dashboard');
  };

  return (
    <>
      <div className="max-w-6xl mx-auto px-4 py-8">
        <h1 className="text-4xl font-bold mb-6 text-center text-blue-900 drop-shadow-lg">
          ðŸŽ¨ Welcome to Yuvi's Artistry ðŸŽ¨
        </h1>

        {/* Carousel Section */}
        <Carousel
          autoPlay
          infiniteLoop
          showThumbs={false}
          showStatus={false}
          className="rounded-2xl overflow-hidden shadow-2xl mb-16"
        >
          {[1, 2, 3, 4, 5].map((num) => (
            <div key={num} className="relative">
              <img
                src={`/images/art${num}.jpg`}
                alt={`Art ${num}`}
                className="h-[500px] w-full object-cover"
              />
              <p className="legend text-xl font-semibold bg-opacity-80 bg-black text-white p-3 rounded-b-xl">
                Art Title {num} - â‚¹{num * 500 + 499}
              </p>
            </div>
          ))}
        </Carousel>

        {/* Role Cards Section */}
        <div className="mt-16 grid grid-cols-1 md:grid-cols-3 gap-8">
          {[
            {
              role: 'Admin',
              description: 'Manage users, artworks, and all platform operations efficiently.',
              icon: 'ðŸ› ï¸',
              color: 'bg-gradient-to-br from-purple-600 to-indigo-600',
            },
            {
              role: 'Artist',
              description: 'Upload and sell your beautiful artworks, manage your gallery.',
              icon: 'ðŸŽ¨',
              color: 'bg-gradient-to-br from-pink-500 to-red-500',
            },
            {
              role: 'Customer',
              description: 'Browse, purchase, and enjoy amazing artwork collections.',
              icon: 'ðŸ›’',
              color: 'bg-gradient-to-br from-green-500 to-teal-500',
            },
          ].map(({ role, description, icon, color }) => (
            <div
              key={role}
              className={`${color} text-white rounded-2xl p-6 flex flex-col justify-between items-center shadow-xl hover:shadow-2xl transform transition-transform hover:-translate-y-2 hover:scale-105 duration-300`}
            >
              <div className="text-6xl mb-4 animate-bounce">{icon}</div>
              <h3 className="text-2xl font-bold mb-2">{role}</h3>
              <p className="mb-4 text-center text-sm">{description}</p>
              <button
                onClick={() => handleNavigate(role)}
                className="mt-auto bg-white text-gray-900 font-bold py-2 px-5 rounded-full shadow-md hover:bg-gray-100 transition duration-300"
              >
                Enter as {role}
              </button>
            </div>
          ))}
        </div>
      </div>

      <style>{`
        .carousel .slide {
          background-color: #f3f4f6;
        }
        .carousel .legend {
          font-size: 1.25rem;
          font-weight: bold;
          text-align: center;
        }
      `}</style>
    </>
  );
}
