import { useEffect, useState } from 'react';
import axios from 'axios';
import { useCart } from '../context/CartContext'; // Adjust path as needed

export default function ArtGallery() {
  const [artworks, setArtworks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { addToCart } = useCart();

  useEffect(() => {
    const fetchArtworks = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/artworks');
        setArtworks(res.data);
      } catch (err) {
        console.error('Failed to fetch artworks:', err);
        setError('Could not load artworks.');
      } finally {
        setLoading(false);
      }
    };

    fetchArtworks();
  }, []);

  if (loading) return <p className="text-center mt-4">Loading artworks...</p>;
  if (error) return <p className="text-center text-red-500">{error}</p>;

  return (
    <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6 p-4">
      {artworks.map((art) => (
        <div key={art._id} className="border border-gray-300 p-4 rounded shadow bg-white">
          <img
            src={art.imageUrl}
            alt={art.title}
            className="w-full h-48 object-cover rounded"
          />
          <h3 className="mt-2 text-lg font-semibold">{art.title}</h3>
          <p className="text-sm text-gray-700">{art.description}</p>
          <p className="font-bold mt-1">â‚¹{art.price}</p>
          <p className="text-sm text-gray-500 mb-2">By: {art.artistId?.name || 'Unknown Artist'}</p>
          <button
            onClick={() => addToCart(art)}
            className="mt-2 px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700"
          >
            Buy
          </button>
        </div>
      ))}
    </div>
  );
}
