import { useEffect, useState } from 'react';
import axios from 'axios';

export default function MyOrders() {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    const fetchOrders = async () => {
      const token = localStorage.getItem('token');
      if (!token) {
        alert('Login required to view orders');
        return;
      }

      try {
        const res = await axios.get('http://localhost:5000/api/orders/my-orders', {
          headers: {
            Authorization: token,
          },
        });

        setOrders(res.data);
      } catch (err) {
        console.error('Failed to fetch orders:', err);
        alert('Failed to load orders');
      }
    };

    fetchOrders();
  }, []);

  return (
    <div>
      <h2>My Orders</h2>
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))', gap: '20px' }}>
        {orders.map((order) => (
          <div key={order._id} style={{ border: '1px solid #ccc', padding: '10px', borderRadius: '8px' }}>
            <img
              src={order.artworkId.imageUrl}
              alt={order.artworkId.title}
              style={{ width: '100%', height: '200px', objectFit: 'cover' }}
            />
            <h3>{order.artworkId.title}</h3>
            <p>{order.artworkId.description}</p>
            <p><strong>â‚¹{order.artworkId.price}</strong></p>
            <p><em>Ordered at: {new Date(order.orderedAt).toLocaleString()}</em></p>
          </div>
        ))}
      </div>
    </div>
  );
}
