import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';

export default function CartPage() {
  const { cartItems, removeFromCart, clearCart } = useCart();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);

  const handlePlaceOrder = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please login to place your order.');
      return;
    }

    try {
      setLoading(true);

      const response = await axios.post(
        'http://localhost:5000/api/orders/place',
        { items: cartItems }, // assuming cartItems is an array of { _id, title, price }
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      clearCart();
      alert('🎉 Order placed successfully!');
      navigate('/my-orders');
    } catch (err) {
      console.error('Order failed:', err);
      alert('❌ Failed to place order. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const totalPrice = cartItems.reduce((total, art) => total + art.price, 0);

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">🛒 Your Cart</h2>
      {cartItems.length === 0 ? (
        <p>No items in cart.</p>
      ) : (
        <>
          <ul className="space-y-4">
            {cartItems.map((art) => (
              <li
                key={art._id}
                className="flex justify-between items-center border p-4 rounded"
              >
                <div>
                  <h3 className="font-semibold">{art.title}</h3>
                  <p>₹{art.price}</p>
                </div>
                <button
                  onClick={() => removeFromCart(art._id)}
                  className="bg-red-500 text-white px-3 py-1 rounded"
                >
                  Remove
                </button>
              </li>
            ))}
          </ul>

          <div className="mt-6 text-lg font-semibold">
            Total: ₹{totalPrice.toFixed(2)}
          </div>

          <button
            onClick={handlePlaceOrder}
            disabled={loading}
            className={`mt-4 bg-green-600 text-white px-5 py-2 rounded ${
              loading ? 'opacity-50 cursor-not-allowed' : ''
            }`}
          >
            {loading ? 'Placing Order...' : 'Place Order'}
          </button>
        </>
      )}
    </div>
  );
}
