import { useState } from 'react';
import axios from 'axios';

export default function Upload() {
  const [title, setTitle] = useState('');
  const [imageFile, setImageFile] = useState(null);
  const [userId, setUserId] = useState('');
  const [price, setPrice] = useState('');
  const [preview, setPreview] = useState('');

  const handleImageUpload = async () => {
    const formData = new FormData();
    formData.append('image', imageFile);
    const res = await axios.post('http://localhost:5000/api/upload/upload', formData);
    return res.data.imageUrl;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const imageUrl = await handleImageUpload();
    await axios.post('http://localhost:5000/api/artworks', {
      title, image: imageUrl, artistId: userId, price
    });
    alert('Uploaded!');
  };

  return (
    <form onSubmit={handleSubmit}>
      <input value={userId} onChange={(e) => setUserId(e.target.value)} placeholder="User ID" />
      <input value={title} onChange={(e) => setTitle(e.target.value)} placeholder="Title" />
      
      {preview && <img src={preview} alt="Preview" width={200} />}
      <input type="number" value={price} onChange={(e) => setPrice(e.target.value)} placeholder="Price (â‚¹)" />
      <button type="submit">Post Artwork</button>
    </form>
  );
}
