import { useParams } from 'react-router-dom';
import { useEffect, useState } from 'react';
import axios from 'axios';

export default function ArtDetails() {
  const { id } = useParams();
  const [art, setArt] = useState(null);
  const [buyerId, setBuyerId] = useState('');
  const [isSold, setIsSold] = useState(false);
  const [loading, setLoading] = useState(true);
  const [buying, setBuying] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchArt = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/artworks/${id}`);
        setArt(res.data);
        setIsSold(res.data.sold);
      } catch (err) {
        console.error('Error fetching artwork:', err);
        setError('Failed to load artwork.');
      } finally {
        setLoading(false);
      }
    };

    fetchArt();
  }, [id]);

  const handleBuy = async () => {
    if (!buyerId.trim()) {
      alert('Please enter your Buyer ID.');
      return;
    }

    try {
      setBuying(true);
      await axios.patch(`http://localhost:5000/api/artworks/${id}/buy`, { buyerId });
      alert('Purchase successful!');
      setIsSold(true);
    } catch (err) {
      console.error('Purchase error:', err);
      alert('Failed to complete purchase.');
    } finally {
      setBuying(false);
    }
  };

  if (loading) return <p className="text-center mt-10">Loading...</p>;
  if (error) return <p className="text-center mt-10 text-red-500">{error}</p>;

  return (
    <div className="max-w-2xl mx-auto p-6 shadow-lg rounded border mt-10">
      <h2 className="text-2xl font-bold mb-4">{art.title}</h2>
      <img
        src={`http://localhost:5000${art.image}`}
        alt={art.title}
        className="w-full max-h-[400px] object-cover rounded mb-4"
      />
      <p className="text-lg mb-2">Price: â‚¹{art.price}</p>
      <p className={`font-semibold mb-4 ${isSold ? 'text-red-600' : 'text-green-600'}`}>
        Status: {isSold ? 'Sold' : 'Available'}
      </p>

      {!isSold && (
        <div className="space-y-4">
          <input
            value={buyerId}
            onChange={(e) => setBuyerId(e.target.value)}
            placeholder="Enter your Buyer ID"
            className="w-full p-2 border rounded"
          />
          <button
            onClick={handleBuy}
            disabled={buying}
            className="w-full bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 disabled:opacity-50"
          >
            {buying ? 'Processing...' : 'Buy Now'}
          </button>
        </div>
      )}
    </div>
  );
}
