import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';

export default function Home() {
  return (
    <>
      <div className="home">
        {/* Header with Background */}
        <div className="bg-blue-900 text-white py-12 backdrop-blur-md bg-opacity-70 shadow-lg">
          <div className="text-center space-y-4">
            <h1 className="text-4xl font-extrabold mb-6 text-white glowing-text">Welcome to Yuvi's Artistry</h1>

            {/* Button Container */}
            <div className="flex justify-center space-x-6 flex-wrap">
              <Button variant="primary" className="btn-custom mb-4 sm:mb-0">Admin</Button>
              <Button variant="secondary" className="btn-custom mb-4 sm:mb-0">Artist</Button>
              <Button variant="success" className="btn-custom mb-4 sm:mb-0">Customer</Button>
            </div>
          </div>
        </div>

        {/* Carousel Section */}
        <div className="max-w-6xl mx-auto px-4 py-8">
          <Carousel
            autoPlay
            infiniteLoop
            showThumbs={false}
            showStatus={false}
            className="mb-10"
          >
            {[1, 2, 3, 4, 5].map((num, idx) => (
              <div key={idx}>
                <img
                  src={`/images/art${num}.jpg`}
                  alt={`Art ${num}`}
                  className="carousel-img"
                />
                <p className="legend">Art Title {num} - â‚¹{num * 500 + 499}</p>
              </div>
            ))}
          </Carousel>
        </div>
      </div>

      <style>{`
        .home {
          min-height: 100vh;
          background: linear-gradient(135deg, #0f0f0f, #1c1c1c);
        }

        /* Glowing title */
        .glowing-text {
          background: linear-gradient(45deg, #0ff, #09f, #3cf);
          -webkit-background-clip: text;
          -webkit-text-fill-color: transparent;
          text-shadow: 0 0 15px rgba(0, 255, 255, 0.5);
          font-family: 'Times New Roman', serif;
          animation: glow 3s ease-in-out infinite alternate;
        }

        @keyframes glow {
          from {
            text-shadow: 0 0 10px #00ffff;
          }
          to {
            text-shadow: 0 0 20px #00ffff, 0 0 30px #00ffff;
          }
        }

        .btn-custom {
          padding: 12px 24px;
          font-size: 1.1rem;
          font-weight: bold;
          border-radius: 12px;
          font-family: 'Times New Roman';
          transition: all 0.4s ease;
          border: none;
          color: #fff;
          animation: float 3s ease-in-out infinite;
        }

        .btn-custom:hover {
          transform: translateY(-5px) scale(1.05);
          box-shadow: 0 8px 16px rgba(255, 255, 255, 0.2);
        }

        @keyframes float {
          0%, 100% {
            transform: translateY(0);
          }
          50% {
            transform: translateY(-6px);
          }
        }

        /* Button Variants with Gradient */
        .btn-custom.btn-primary {
          background: linear-gradient(45deg, #00c6ff, #0072ff);
        }

        .btn-custom.btn-secondary {
          background: linear-gradient(45deg, #ff6e7f, #bfe9ff);
        }

        .btn-custom.btn-success {
          background: linear-gradient(45deg, #00f260, #0575e6);
        }

        /* Carousel Styling */
        .carousel .slide {
          background: transparent;
          display: flex;
          align-items: center;
          justify-content: center;
          height: 80vh;
        }

        .carousel-img {
          width: 100%;
          height: 80vh;
          object-fit: cover;
          border-radius: 20px;
          box-shadow: 0 10px 30px rgba(0,0,0,0.6);
          transition: transform 0.4s ease, box-shadow 0.4s ease;
        }

        .carousel .slide:hover .carousel-img {
          transform: scale(1.03);
          box-shadow: 0 16px 40px rgba(0,0,0,0.8);
        }

        .carousel .legend {
          font-size: 1.5rem;
          font-weight: 600;
          background: rgba(0, 0, 0, 0.7);
          color: #fff;
          padding: 12px 20px;
          border-radius: 12px;
          text-shadow: 1px 1px 6px rgba(255, 255, 255, 0.2);
          backdrop-filter: blur(6px);
        }

        /* Responsive Design */
        @media (max-width: 768px) {
          .carousel-img {
            height: 70vh;
          }
          .glowing-text {
            font-size: 2.5rem;
          }
          .carousel .legend {
            font-size: 1.2rem;
          }
        }

        @media (max-width: 480px) {
          .btn-custom {
            font-size: 0.95rem;
            padding: 10px 18px;
          }
          .carousel-img {
            height: 65vh;
          }
          .carousel .legend {
            font-size: 1rem;
          }
        }
      `}</style>
    </>
  );
}
