// src/pages/CartPage.js
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';

export default function CartPage() {
  const { cartItems, removeFromCart, clearCart } = useCart();
  const [orderMessage, setOrderMessage] = useState('');
  const navigate = useNavigate();

  const handlePlaceOrder = async () => {
    if (!cartItems || cartItems.length === 0) {
      setOrderMessage('Cart is empty or not loaded properly.');
      return;
    }

    const items = cartItems.map((item) => ({
      artwork: item._id,
    }));

    try {
      await axios.post(
        'http://localhost:5000/api/orders/place',
        { items },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        }
      );
      setOrderMessage('‚úÖ Order placed successfully!');
      clearCart();
      setTimeout(() => navigate('/my-orders'), 1500);
    } catch (err) {
      setOrderMessage(`‚ùå Order failed: ${err.response?.data?.message || 'Server error'}`);
      console.error('Order failed:', err);
    }
  };

  return (
    <div className="min-h-screen bg-black text-white relative overflow-hidden p-6">
      {/* Soft abstract background gradient overlay */}
      <div className="absolute top-0 left-0 w-full h-full bg-gradient-to-br from-[#111] via-[#222] to-[#000] opacity-80 pointer-events-none z-0"></div>
      <div className="absolute top-[-30%] right-[-10%] w-[400px] h-[400px] bg-pink-600/10 rounded-full blur-3xl"></div>
      <div className="absolute bottom-[-20%] left-[-10%] w-[300px] h-[300px] bg-purple-700/10 rounded-full blur-2xl"></div>

      <div className="relative z-10 max-w-4xl mx-auto fade-in">
        <h1 className="text-4xl font-extrabold text-center mb-10 tracking-wide elegant-text">
          üõí Your Cart
        </h1>

        {orderMessage && (
          <p className="text-center mb-6 text-rose-400 text-lg font-medium">{orderMessage}</p>
        )}

        {cartItems.length === 0 ? (
          <div className="text-center mt-24 text-gray-400">
            <p className="text-2xl">No items yet!</p>
            <p className="text-sm mt-2 text-gray-600">Start collecting your favorite artworks.</p>
          </div>
        ) : (
          <div className="space-y-6 max-h-[70vh] overflow-y-auto scrollbar-thin scrollbar-thumb-gray-600 pr-2">
            {cartItems.map((item) => (
              <div
                key={item._id}
                className="p-4 rounded-xl bg-white/5 backdrop-blur-sm shadow-md border border-white/10 hover:scale-[1.015] transition-all duration-300 group"
              >
                <div className="flex justify-between items-center">
                  <div>
                    <h2 className="text-xl font-semibold text-gray-100 group-hover:text-pink-400 transition">
                      {item.title}
                    </h2>
                    <p className="text-gray-400 text-sm mt-1">‚Çπ{item.price}</p>
                  </div>
                  <button
                    onClick={() => removeFromCart(item._id)}
                    className="text-rose-500 hover:text-rose-700 font-medium transition duration-200"
                  >
                    ‚úï Remove
                  </button>
                </div>
              </div>
            ))}

            <div className="text-center mt-12">
              <button
                onClick={handlePlaceOrder}
                className="bg-gradient-to-r from-pink-600 via-purple-600 to-indigo-600 text-white px-8 py-3 text-lg font-semibold rounded-full shadow-lg hover:shadow-pink-500/30 transition-all duration-300 tracking-wide hover:scale-105"
              >
                üöÄ Place Order
              </button>
            </div>
          </div>
        )}
      </div>

      <style>{`
        .fade-in {
          animation: fadeIn 0.7s ease-out;
        }

        .elegant-text {
          background: linear-gradient(to right, #f472b6, #c084fc, #60a5fa);
          -webkit-background-clip: text;
          -webkit-text-fill-color: transparent;
        }

        @keyframes fadeIn {
          from {
            opacity: 0;
            transform: translateY(10px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
      `}</style>
    </div>
  );
}
