import { useEffect, useState } from 'react';
import axios from 'axios';

export default function Home() {
  const [arts, setArts] = useState([]);

  useEffect(() => {
    const fetchArts = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/artworks');
        setArts(res.data);
      } catch (err) {
        console.error('Failed to load artworks', err);
      }
    };
    fetchArts();
  }, []);

  const handleBuy = async (artworkId) => {
    const token = localStorage.getItem('token');
    if (!token) return alert('You must be logged in to place an order.');
  
    try {
      await axios.post('http://localhost:5000/api/orders', { artworkId }, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('Order placed successfully!');
    } catch (err) {
      console.error('Order failed:', err);
      alert('Failed to place order');
    }
  };
  

  return (
    <div className="max-w-6xl mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-6">Gallery</h1>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
        {arts.map((art) => (
          <div key={art._id} className="border p-4 rounded shadow">
            <img src={art.imageUrl} alt={art.title} className="w-full h-48 object-cover rounded mb-2" />
            <h2 className="text-xl font-bold">{art.title}</h2>
            <p className="text-gray-600">â‚¹{art.price}</p>
            <p className="text-sm">{art.description}</p>
            <button
  onClick={() => handleBuy(art._id)}
  className="mt-2 bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
>
  Buy Now
</button>

          </div>
        ))}
      </div>
    </div>
  );
}
