import React from 'react';
import { useCart } from '../context/CartContext'; // Adjust path if needed
import { Link } from 'react-router-dom';

export default function Cart() {
  const { cartItems, removeFromCart, clearCart } = useCart();

  const total = cartItems.reduce((sum, art) => sum + art.price, 0);

  if (cartItems.length === 0) {
    return (
      <div className="p-8 text-center">
        <h2 className="text-2xl font-bold mb-4">Your cart is empty ðŸ›’</h2>
        <Link to="/gallery" className="text-blue-600 underline">Go to Gallery</Link>
      </div>
    );
  }

  return (
    <div className="p-8">
      <h2 className="text-3xl font-bold mb-6">Your Cart</h2>
      <div className="grid gap-6">
        {cartItems.map((item) => (
          <div key={item._id} className="border rounded-lg p-4 shadow flex items-center gap-4">
            <img src={item.imageUrl} alt={item.title} className="w-24 h-24 object-cover rounded" />
            <div className="flex-1">
              <h3 className="text-xl font-semibold">{item.title}</h3>
              <p>â‚¹{item.price}</p>
              <button
                onClick={() => removeFromCart(item._id)}
                className="mt-2 px-3 py-1 bg-red-500 text-white rounded hover:bg-red-600"
              >
                Remove
              </button>
            </div>
          </div>
        ))}
      </div>

      <div className="mt-6 text-right">
        <h3 className="text-xl font-semibold">Total: â‚¹{total}</h3>
        <button
          onClick={clearCart}
          className="mr-4 px-4 py-2 bg-gray-500 text-white rounded hover:bg-gray-600"
        >
          Clear Cart
        </button>
        <Link to="/checkout">
          <button className="px-6 py-2 bg-green-600 text-white rounded hover:bg-green-700">
            Proceed to Checkout
          </button>
        </Link>
      </div>
    </div>
  );
}
