// src/pages/AdminDashboard.js
import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

export default function AdminDashboard() {
  const [artworks, setArtworks] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/admin');
      return;
    }
    fetchArtworks(token);
  }, [navigate]);

  const fetchArtworks = async (token) => {
    try {
      const res = await axios.get('http://localhost:5000/api/artworks', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setArtworks(res.data);
    } catch (err) {
      console.error(err);
      navigate('/login');
    }
  };

  const handleDelete = async (id) => {
    const token = localStorage.getItem('token');
    if (!window.confirm('Are you sure you want to delete this artwork?')) return;

    try {
      await axios.delete(`http://localhost:5000/api/artworks/${id}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setArtworks(artworks.filter((art) => art._id !== id));
    } catch (err) {
      alert('Failed to delete artwork');
      console.error(err);
    }
  };

  const handleEdit = (id) => {
    navigate(`/admin/edit-artwork/${id}`);
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <header className="mb-6 flex justify-between items-center">
        <h2 className="text-3xl font-extrabold text-gray-800">ðŸŽ¨ Admin Dashboard</h2>
        <button
          onClick={() => navigate('/admin/upload')}
          className="bg-green-600 hover:bg-green-700 text-white font-semibold px-4 py-2 rounded"
        >
          + Upload New Artwork
        </button>
      </header>

      <section className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {artworks.map((art) => (
          <div key={art._id} className="bg-white rounded-xl shadow-md overflow-hidden hover:shadow-lg transition-shadow">
            <img
              src={art.imageUrl}
              alt={art.title}
              className="w-full h-56 object-cover"
            />
            <div className="p-4">
              <h3 className="text-xl font-semibold text-gray-800">{art.title}</h3>
              <p className="text-sm text-gray-500 mt-1 mb-2">{art.category || 'Uncategorized'}</p>
              <div className="flex justify-between items-center text-sm text-gray-700">
                <span>â‚¹{art.price}</span>
                <span className="text-xs bg-blue-100 text-blue-800 px-2 py-0.5 rounded">
                  {art.inStock ? 'In Stock' : 'Out of Stock'}
                </span>
              </div>
              <div className="flex mt-4 space-x-2">
                <button
                  onClick={() => handleEdit(art._id)}
                  className="flex-1 bg-yellow-400 hover:bg-yellow-500 text-white px-3 py-1 rounded"
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDelete(art._id)}
                  className="flex-1 bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded"
                >
                  Delete
                </button>
              </div>
            </div>
          </div>
        ))}
      </section>

      {artworks.length === 0 && (
        <p className="text-center text-gray-600 mt-10">No artworks uploaded yet.</p>
      )}
    </div>
  );
}
