import { useEffect, useState } from 'react';
import axios from 'axios';

export default function ArtGallery() {
  const [artworks, setArtworks] = useState([]);

  useEffect(() => {
    const fetchArtworks = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/artworks');
        setArtworks(res.data);
      } catch (err) {
        console.error('Failed to fetch artworks:', err);
      }
    };

    fetchArtworks();
  }, []);

  const handleBuy = async (artworkId) => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please log in to place an order.');
      return;
    }

    try {
      await axios.post(
        'http://localhost:5000/api/orders',
        { artworkId },
        {
          headers: {
            Authorization: token,
          },
        }
      );

      alert('Order placed successfully!');
    } catch (err) {
      console.error('Order failed:', err);
      alert('Failed to place order.');
    }
  };

  return (
    <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))', gap: '20px' }}>
      {artworks.map((art) => (
        <div key={art._id} style={{ border: '1px solid #ccc', padding: '10px', borderRadius: '8px' }}>
          <img
            src={art.imageUrl}
            alt={art.title}
            style={{ width: '100%', height: '200px', objectFit: 'cover' }}
          />
          <h3>{art.title}</h3>
          <p>{art.description}</p>
          <p><strong>â‚¹{art.price}</strong></p>
          <p>By: {art.artistId?.name || 'Unknown Artist'}</p>
          <button onClick={() => handleBuy(art._id)} style={{ marginTop: '10px' }}>
            Buy
          </button>
        </div>
      ))}
    </div>
  );
}
